mapper.select:
select	count(*) cnt
from	member m, member_detail md,
		(
		select	mem_num, max(input_date) recent_login_date
		from	MEMBER_LOGIN_LOG
		where	input_date <= sysdate
		group by mem_num
		) ml
where	md.mem_num(+) = m.mem_num and ml.MEM_NUM(+) = m.MEM_NUM

	
		and md.MEM_ID like '%' || #{keyword} || '%'
	
	
		and nick like '%' || #{keyword} || '%'
	
 

	and m.join_date >= #{startDate} 


	and m.join_date <= #{endDate} 



	and md.gender = #{gender} 



	and m.LOGIN_FLAG = #{loginFlag} 



	and MEM_STATUS like '%' || #{memStatus} || '%'

select	RNUM,MEM_NUM,NICK,MEM_ID,NAME,IMG,LOGIN_FLAG,RECENT_LOGIN_DATE,JOIN_DATE,MEM_STATUS
from	(
		select	row_number() over(order by m.nick) rnum, m.MEM_NUM, m.nick, md.MEM_ID, md.name, m.img, m.LOGIN_FLAG, ml.recent_login_date, m.JOIN_DATE, m.MEM_STATUS
		from	member m, member_detail md,
				(
				select	mem_num, max(input_date) recent_login_date
				from	MEMBER_LOGIN_LOG
				where	input_date <= sysdate
				group by mem_num
				) ml
		where	md.mem_num(+) = m.mem_num and ml.MEM_NUM(+) = m.MEM_NUM
				
					
						and md.MEM_ID like '%' || #{keyword} || '%'
					
					
						and nick like '%' || #{keyword} || '%'
					
				 
				
					and m.join_date >= #{startDate} 
				
				
					and m.join_date <= #{endDate} 
				
				
				
					and md.gender = #{gender} 
				
				
				
					and m.LOGIN_FLAG = #{loginFlag} 
				
				
				
					and MEM_STATUS like '%' || #{memStatus} || '%' 
				
		)
where	rnum between #{startNum} and #{endNum}

select	IP,OS,BROWSER,MEM_STATUS,INPUT_DATE
from	(
		select	row_number() over(order by input_date desc) rnum, ip, os, browser, mem_status, input_date
		from	MEMBER_LOGIN_LOG
		where	mem_num = #{keyword} and input_date <= sysdate
		)
where 	rnum between #{startNum} and #{endNum}

select GOODS_NUM,TITLE,PRICE,IMG,STEAM_FLAG,TIME_AGO, input_date from ( select row_number() over(order by s.input_date desc) rnum, g.goods_num, g.name title, g.price, gi.name img, s.steam_flag, s.input_date, case when trunc(g.input_date) = trunc(sysdate) then '오늘' else trunc(sysdate) - trunc(g.input_date) || ' 일 전' end time_ago, g.detail, m.nick from steam s, goods g, GOODS_IMG gi, member m where s.GOODS_NUM = g.GOODS_NUM and gi.GOODS_NUM = g.GOODS_NUM and g.mem_num_sell = m.mem_num and s.mem_num = #{memNum}
and g.name like '%' || #{keyword} || '%'
and g.detail like '%' || #{keyword} || '%'
and m.nick like '%' || #{keyword} || '%'
) where rnum between #{startNum} and #{endNum}

select count(*) from VIEW_MGR_REVIEW where mem_num_buy = #{memNum}
and GOODS_TITLE like '%' || #{keyword} || '%'
and NICK_SELL like '%' || #{keyword} || '%'
and CONTENT like '%' || #{keyword} || '%'
and review_input_date >= #{startDate}
and review_input_date < to_date(#{endDate}, 'yyyy-mm-dd') + 1

select	GOODS_NUM,
		case
			when length(GOODS_TITLE) > 20 then substr(GOODS_TITLE, 1, 17) || '...'
			else GOODS_TITLE
		end GOODS_TITLE,
		GOODS_IMG, MEM_NUM_SELL, NICK_SELL,
		case
			when length(content) > 15 then substr(CONTENT, 1, 12) || '...'
			else content
		end content, 
		MEM_NUM_BUY, NICK_BUY, STAR, REVIEW_INPUT_DATE, DELETE_FLAG
from	(
		select	row_number() over(order by REVIEW_INPUT_DATE desc) rnum, GOODS_NUM, GOODS_TITLE, GOODS_IMG, MEM_NUM_SELL, NICK_SELL, CONTENT, MEM_NUM_BUY, NICK_BUY, STAR, REVIEW_INPUT_DATE, DELETE_FLAG
		from	VIEW_MGR_REVIEW
		where	mem_num_buy = #{memNum} 
				
					
						and GOODS_TITLE like '%' || #{keyword} || '%'
					
					
						and NICK_SELL like '%' || #{keyword} || '%'
					
					
						and CONTENT like '%' || #{keyword} || '%'
					
				
				
					and review_input_date >= #{startDate} 
				
				
					and review_input_date < to_date(#{endDate}, 'yyyy-mm-dd') + 1
				
		)
where	rnum between #{startNum} and #{endNum}

select	rnum,TITLE,CONTENTS,INPUT_DATE,ANSWER_FLAG,ANSWER_DATE
from	(
		select	row_number() over(order by input_date desc) RNUM,TITLE,CONTENTS,INPUT_DATE,ANSWER_FLAG,ANSWER_DATE
		from	inquiry
		where	mem_num = #{memNum} 
				
					
						and TITLE like '%' || #{keyword} || '%'
					
					
						and CONTENTS like '%' || #{keyword} || '%'
					
				
				
					and input_date >= to_date(#{startDate}, 'yyyy-mm-dd') 
				
				
					and input_date < to_date(#{endDate}, 'yyyy-mm-dd') + 1
				
				
					
						
							and answer_Flag = 'N'
						
						
							and answer_Flag = 'Y'
						
					
				
		)
where	rnum between #{startNum} and #{endNum}
